declare module 'base64-img' {
    /**
     * Get image file base64 data asynchronously.
     * @param filename - The path to the image file.
     * @param callback - The callback function that handles the base64 data.
     */
    export function base64(
        filename: string,
        callback: (err: Error | null, data: string) => void
    ): void;

    /**
     * Get image file base64 data synchronously.
     * @param filename - The path to the image file.
     * @returns The base64 data of the image.
     */
    export function base64Sync(filename: string): string;

    /**
     * Get base64 from a URL asynchronously.
     * @param url - The URL of the image.
     * @param callback - The callback function that handles the base64 data.
     */
    export function requestBase64(
        url: string,
        callback: (err: Error | null, res: any, data: string) => void
    ): void;

    /**
     * Convert image base64 data to an image file asynchronously.
     * @param data - The base64 image data.
     * @param destpath - The destination directory for the image file.
     * @param name - The name of the image file (without extension).
     * @param callback - The callback function that handles the image file path.
     */
    export function img(
        data: string,
        destpath: string,
        name: string,
        callback: (err: Error | null, filepath: string) => void
    ): void;

    /**
     * Convert image base64 data to an image file synchronously.
     * @param data - The base64 image data.
     * @param destpath - The destination directory for the image file.
     * @param name - The name of the image file (without extension).
     * @returns The file path of the saved image.
     */
    export function imgSync(
        data: string,
        destpath: string,
        name: string
    ): string;
}
